import styled from 'styled-components';
import React, { useState } from 'react';

const ListItem = styled.div`
  padding: 15px;
  border-bottom: 1px solid #dbdbdb;
  cursor: pointer;
  display: flex;
  align-items: center;

  background-color: ${props => props.isSelected ? '#f0f0f0' : 'inherit'};

  &:hover {
    background-color: #f0f0f0;
  }
`;

const Avatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: #bbb;
  margin-right: 10px;
`;

const ChatListItem = ({ chat, selectChat }) => {
  const [isSelected, setIsSelected] = useState(false);

  const handleClick = () => {
    selectChat(chat.id); // 선택된 채팅 ID 전달
    setIsSelected(true); // 클릭 시 isSelected 상태를 true로 변경
  };
  
  return (
    // <ListItem onClick={() => selectChat(chat.id)}>
    <ListItem onClick={handleClick} isSelected={isSelected}>
      <Avatar />
      {chat.name}
    </ListItem>
  );
};

export default ChatListItem;
