import React from 'react';
import styled from 'styled-components';

const SidebarContainer = styled.div`
  background-color: #fafafa;
  width: 280px;
  height: 100vh;
  padding: 20px;
  position: fixed;
  top: 0;
  left: 0;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
  z-index: 100;
`;

const ProfileSection = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const ProfileImage = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin-right: 10px;
`;

const ProfileInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

const SidebarMenu = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const SidebarMenuItem = styled.li`
  padding: 10px 0;
  cursor: pointer;
  &:hover {
    background-color: #f0f0f0;
  }
`;

const InstagramSidebar = () => {
  // 간단한 사용자 정보
  const userProfile = {
    username: 'example_user',
    profileImage: 'https://example.com/profile-image.jpg',
  };

  // 사이드바 메뉴 항목
  const menuItems = [
    { id: 1, label: 'Home' },
    { id: 2, label: 'Explore' },
    { id: 3, label: 'Notifications' },
    { id: 4, label: 'Messages' },
    { id: 5, label: 'Profile' },
  ];

  return (
    <SidebarContainer>
      <ProfileSection>
        <ProfileImage src={userProfile.profileImage} alt="Profile" />
        <ProfileInfo>
          <strong>{userProfile.username}</strong>
          <span>@{userProfile.username}</span>
        </ProfileInfo>
      </ProfileSection>
      <SidebarMenu>
        {menuItems.map((item) => (
          <SidebarMenuItem key={item.id}>{item.label}</SidebarMenuItem>
        ))}
      </SidebarMenu>
    </SidebarContainer>
  );
};

export default InstagramSidebar;
