import React, { useState } from 'react';
import styled from 'styled-components';
import ChatWindow from './components//ChatWindow';
import MessageInput from './components/MessageInput';
import ChatList from './components/ChatList';
import axios from "axios";
const AppContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #fafafa;
`;

const AppBox = styled.div`
  width: 900px;
  height: 600px;
  border: 1px solid #dbdbdb;
  border-radius: 10px;
  background-color: #fff;
  display: flex;
  flex-direction: row;
`;

const ChatSection = styled.div`
  display: flex;
  flex-direction: column;
  flex: 2;
`;
function a(myId){

  axios
    .get("http://127.0.0.1:8000", {
      params: {
        select: 1,
        myid: myId,
      },
    })
    .then((response) => {
      return response.data;
    }).catch(error => {
      console.error('Error fetching chats:', error);
    });
}
const App = () => {
  // 로그인 하면 myid 받기
  const [myId, setId] = useState(6)

  // const [chats, setChats] = useState([
  //   { id: 1, name: 'John Doe', messages: [] },
  //   { id: 2, name: 'Jane Smith', messages: [] },
  //   { id: 3, name: 'John', messages: [] },
  //   { id: 4, name: 'Minji', messages: [] },
  // ]);
  const [chats, setChats] = useState(a(myId));
  const [selectedChatId, setSelectedChatId] = useState(null);

  const addMessage = (message) => {
    setChats(chats.map(chat => {
      if (chat.id === selectedChatId) {
        return { ...chat, messages: [...chat.messages, message] };
      }
      return chat;
    }));
  };

  const selectChat = (id) => {
    setSelectedChatId(id);
  };

  const selectedChat = chats.find(chat => chat.id === selectedChatId);

  return (
    <AppContainer>
      <AppBox>
        <ChatList chats={chats} selectChat={selectChat} />
        <ChatSection>
          {selectedChat && (
            <>
              <ChatWindow messages={selectedChat.messages} />
              <MessageInput addMessage={addMessage} />
            </>
          )}
        </ChatSection>
      </AppBox>
    </AppContainer>
  );
};

export default App;
